INSERT INTO `questions` VALUES ('16', 'Theo bạn, khi người khác góp ý chân thành về cách xử lý công việc của bạn chưa thấu đáo, bạn không nên tự ái vì?', 'Điều đó thể hiện tinh thần cầu tiến của bạn', 'Điều đó thể hiện sự tôn trọng với người góp ý', 'Điều đó thể hiện sự tiếp thu ý kiến', 'Cả 3 đáp án trên', '4', '1', '2019-07-08 13:43:35', '2019-07-08 13:43:35');
