INSERT INTO `questions` VALUES ('45', 'Hãy điền từ còn thiếu vào câu “…………nói lên tất cả”  để hoàn thiện nội dung theo nguyên tắc văn hóa tối thượng của TNG:', 'Chữ số', 'Con số', 'Bảng số', 'Chữ cái', '2', '1', '2019-07-08 13:43:35', '2019-07-08 13:43:35');
INSERT INTO `questions` VALUES ('46', 'Hãy điền từ còn thiếu vào câu “………..nói lên………..” để hoàn thiện nội dung theo nguyên tắc văn hóa tối thượng của TNG:', 'Con số/tất cả', 'Bảng số/kết quả', 'Con số/ con người bạn', 'Con số/kết quả', '1', '1', '2019-07-08 13:43:35', '2019-07-08 13:43:35');
